export default class extends Vue {
  tutorialContent = `%3Cp%3EShivneri%20is%20a%20MVC%20framework%20which%20means%20any%20route%20will%20target%20a%20controller%20method.%20So%20route%20in%20Shivneri%20has%20two%20parts%20-%20%3C/p%3E%0A%3Cul%3E%0A%3Cli%3EController%20-%20A%20class%3C/li%3E%0A%3Cli%3EWorker%20-%20A%20method%20inside%20a%20class%3C/li%3E%0A%3C/ul%3E%0A%3Cp%3E%3Cbr%3E%3C/p%3E%0A%3Ch2%20id=%22howtodecidewhatpartofroutewilltargetcontrollerandworker%22%3EHow%20to%20decide%20what%20part%20of%20route%20will%20target%20controller%20and%20worker%20?%3C/h2%3E%0A%3Cp%3E%3Cbr%3E%0AConsider%20an%20url%20-%20%22abc.com/user/add%22%20and%20let's%20split%20it%20-%20%3C/p%3E%0A%3Cul%3E%0A%3Cli%3Eabc.com%20-%20domain%20of%20the%20website%20%3C/li%3E%0A%3Cli%3E/user%20-%20controller%20route%3C/li%3E%0A%3Cli%3E/add%20-%20worker%20route%3C/li%3E%0A%3C/ul%3E%0A%3Cp%3E%3Cbr%3E%0ALet's%20take%20another%20example%20-%20%22abc.com/user/1/2/3%22%20and%20split%20it%20again%20-%20%3C/p%3E%0A%3Cul%3E%0A%3Cli%3Eabc.com%20-%20domain%20of%20the%20website%3C/li%3E%0A%3Cli%3E/user%20-%20controller%20route%3C/li%3E%0A%3Cli%3E/1/2/3%20-%20worker%20route%3C/li%3E%0A%3C/ul%3E%0A%3Cp%3E%3Cbr%3E%0Abut%20you%20can%20also%20make%20some%20part%20of%20worker%20route%20as%20controller%20depends%20upon%20the%20requirement%3C/p%3E%0A%3Cp%3Ee.g%20-%20The%20above%20route%20can%20be%20also%20breaked%20as%20-%20%3C/p%3E%0A%3Cul%3E%0A%3Cli%3Eabc.com%20-%20domain%20of%20the%20website%3C/li%3E%0A%3Cli%3E/user/1%20-%20controller%20route%3C/li%3E%0A%3Cli%3E/2/3%20-%20worker%20route%3C/li%3E%0A%3C/ul%3E%0A%3Cp%3E%3Cbr%3E%0ALet's%20see%20how%20the%20route%20is%20configured%20in%20Shivneri%20-%20%3C/p%3E%0A%3Ch2%20id=%22controllerroute%22%3EController%20Route%3C/h2%3E%0A%3Cp%3E%3Cbr%3E%0AIn%20order%20to%20create%20a%20controller%20route%20we%20must%20have%20a%20%3Ca%20href=%22/tutorial/controller%22%3Econtroller%3C/a%3E.%20This%20controller%20is%20mapped%20with%20a%20path.%3C/p%3E%0A%3Cp%3Ee.g%20-%20Consider%20you%20have%20created%20a%20controller,%20let's%20say%20-%20%22UserController%22.%3C/p%3E%0A%3Cp%3EYou%20need%20to%20map%20Controller%20with%20a%20path.%20%3C/p%3E%0A%3Cpre%3E%3Ccode%3EShivneri.routes%20=%20%5B%7B%0A%20%20%20%20controller:%20DefaultController,%0A%20%20%20%20path:%20%20%20%20%20%20%20%22/user%22,%0A%7D%5D%0A%3C/code%3E%3C/pre%3E%0A%3Cp%3EIn%20the%20above%20example%20we%20are%20mapping%20%22UserContoller%22%20with%20a%20path%20%22/user%22.%3C/p%3E%0A%3Cp%3E%3Cbr%3E%3C/p%3E%0A%3Ch2%20id=%22workerroute%22%3EWorker%20Route%3C/h2%3E%0A%3Cp%3E%3Cbr%3E%0AWorker%20route%20is%20mapped%20to%20a%20%3Ca%20href=%22/tutorial/worker%22%3EWorker%3C/a%3E%20inside%20Controller.%0Ae.g%20-%20%3C/p%3E%0A%3Cp%3E%3Cstrong%3E%3Cu%3ECreating%20Worker%20Route%3C/u%3E%3C/strong%3E%0A%3Cbr%3E%3Cbr%3E%0AWhen%20you%20add%20annotation%20%22Worker%22%20to%20a%20method%20-%20it%20is%20marked%20as%20end%20point%20with%20route%20%22/#%7Bmethod_name%7D%22.%3C/p%3E%0A%3Cpre%3E%3Ccode%3Eclass%20UserController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker%5D%0A%20%20%20%20def%20add%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20is%20called%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E%0A%3Cp%3ESo,%20When%20url%20will%20be%20-%20%22abc.com/user/add%22%20then%20add%20method%20will%20be%20called.%3C/p%3E%0A%3Cdiv%20class=%22top-border%22%3E%3Cbr%3E%3C/div%3E%0A%3Cp%3E%3Cstrong%3E%3Cu%3ECustomizing%20route%3C/u%3E%3C/strong%3E%0A%3Cbr%3E%3Cbr%3E%0ABy%20default%20route%20is%20name%20of%20method,%20but%20you%20can%20customize%20the%20route%20using%20annotation%20%22Route%22.%3C/p%3E%0A%3Cpre%3E%3Ccode%3Eclass%20UserController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker%5D%0A%20%20%20%20@%5BRoute(%22/add-user%22)%5D%0A%20%20%20%20def%20add%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20is%20called%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E%0A%3Cp%3EYou%20can%20see%20in%20the%20code%20,%20we%20have%20used%20'Route'%20annotation%20to%20override%20the%20route.%20So,%20now%20when%20url%20will%20be%20-%20%22abc.com/user/add-user%22%20then%20add%20method%20will%20be%20called.%3C/p%3E%0A%3Cdiv%20class=%22top-border%22%3E%3Cbr%3E%3C/div%3E%0A%3Cp%3E%3Cstrong%3E%3Cu%3ERestricting%20the%20worker%20based%20on%20http%20method%3C/u%3E%3C/strong%3E%0A%3Cbr%3E%3Cbr%3E%3C/p%3E%0A%3Cp%3ECurrently%20with%20any%20of%20http%20methods%20(GET,POST,%20etc.)%20%22add%22%20method%20will%20be%20hit.%20But%20let's%20say%20you%20want%20to%20hit%20this%20only%20when%20http%20method%20is%20POST.%3C/p%3E%0A%3Cp%3EIn%20this%20case%20-%20you%20need%20to%20provide%20desired%20http%20methods%20to%20Worker%20annotation.%20Let's%20see%20an%20example%20-%20%3C/p%3E%0A%3Cpre%3E%3Ccode%3Eclass%20UserController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker(%22POST%22)%5D%0A%20%20%20%20@%5BRoute(%22/add-user%22)%5D%0A%20%20%20%20def%20add%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20is%20called%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E%0A%3Cdiv%20class=%22top-border%22%3E%3Cbr%3E%3C/div%3E%0A%3Cp%3E%3Cstrong%3E%3Cu%3ESingle%20route%20&%20multiple%20http%20method%3C/u%3E%3C/strong%3E%0A%3Cbr%3E%3Cbr%3E%3C/p%3E%0A%3Cp%3EHow%20about%20you%20want%20to%20create%20a%20route%20%22add%22%20which%20will%20hit%20different%20worker%20based%20on%20http%20methods.%20This%20pattern%20is%20highly%20used%20in%20rest%20api%20implementation%20.%20Let's%20see%20an%20example%20-%3C/p%3E%0A%3Cpre%3E%3Ccode%3Eclass%20UserController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker(%22GET%22)%5D%0A%20%20%20%20@%5BRoute(%22/add%22)%5D%0A%20%20%20%20def%20add_with_get%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20with%20method%20get%20is%20called%22)%0A%20%20%20%20end%0A%0A%20%20%20%20@%5BWorker(%22POST%22)%5D%0A%20%20%20%20@%5BRoute(%22/add%22)%5D%0A%20%20%20%20def%20add_with_post%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20with%20method%20post%20is%20called%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E%0A%3Cp%3EYou%20should%20take%20a%20look%20at%20rest%20example%20-%20%3Ca%20href=%22https://github.com/ujjwalguptaofficial/shivneri-examples/tree/master/rest%22%3Ehttps://github.com/ujjwalguptaofficial/shivneri-examples/tree/master/rest%3C/a%3E%3C/p%3E%0A%3Cdiv%20class=%22top-border%22%3E%3Cbr%3E%3C/div%3E%0A%3Cp%3E%3Cstrong%3E%3Cu%3EParams%20in%20route%3C/u%3E%3C/strong%3E%0A%3Cbr%3E%3Cbr%3E%0A%3Cb%3E1.%20Constant%20param%20:%20%3C/b%3E%0A%3Cbr%3E%3Cbr%3E%0ASo%20far%20we%20have%20created%20simple%20route%20like%20-%20%22abc.com/user/add%22.%20How%20about%20little%20more%20complex%20route%20-%20%22abc.com/user/add/1%22.%20%3C/p%3E%0A%3Cp%3EFor%20this,%20we%20just%20need%20to%20modify%20our%20route.%3C/p%3E%0A%3Cpre%3E%3Ccode%3Eclass%20UserController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker(%22POST%22)%5D%0A%20%20%20%20@%5BRoute(%22/add-user/1%22)%5D%0A%20%20%20%20def%20add%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20is%20called%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E%0A%3Cp%3E%3Cb%3E2.%20Variable%20param%20:%3C/b%3E%20%3Cbr%3E%3Cbr%3EIn%20the%20url%20-%20you%20can%20see%20%221%22%20is%20the%20constant%20value%20but%20for%20certain%20requirements%20-%20the%20place%20where%201%20is%20present%20can%20be%20any%20value.%20%3C/p%3E%0A%3Cp%3Ee.g%20-%20%22abc.com/user/add/2%22,%20%22abc.com/user/add/hi%22,%20%22abc.com/user/add/1000%22%20etc.%3C/p%3E%0A%3Cp%3EBasically%20a%20part%20of%20the%20url%20is%20a%20variable%20which%20can%20hold%20any%20value.%3C/p%3E%0A%3Cp%3EYou%20can%20declare%20a%20variable%20in%20a%20route%20by%20using%20%3Ccode%3E%7B%7D%3C/code%3E.%20Let's%20see%20-%20%3C/p%3E%0A%3Cpre%3E%3Ccode%3Eclass%20UserController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker(%22POST%22)%5D%0A%20%20%20%20@%5BRoute(%22/add-user/%7Bvalue%7D%22)%5D%0A%20%20%20%20def%20add%0A%20%20%20%20%20%20%20%20param_value%20=%20param%5B%22value%22%5D%20#%20param%20is%20hash%20where%20key%20is%20string%20&amp;%20value%20is%20JSON::Any%0A%20%20%20%20%20%20%20%20return%20text_result(%22add%20is%20called%20with%20param%20#%7Bparam_value%7D%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E%0A%3Cp%3E%3Cb%3E3.%20Variable%20&%20Constant%20:%3C/b%3E%20%3Cbr%3E%3Cbr%3EHow%20about%20a%20route%20where%20you%20want%20to%20return%20some%20file%20based%20on%20some%20extension.%20%3C/p%3E%0A%3Cp%3Ee.g%20-%20%22abc.com/file/scripts/jquery.js%22%20.%20In%20this%20example%20-%20%22jquery%22%20is%20variable%20value%20and%20%22.js%22%20is%20constant%20value%3C/p%3E%0A%3Cp%3ESo%20here%20:%3C/p%3E%0A%3Cul%3E%0A%3Cli%3Econtroller%20route%20is%20-%20'file'%3C/li%3E%0A%3Cli%3Eworker%20route%20is%20-%20'scripts/jquery.js'%3C/li%3E%0A%3C/ul%3E%0A%3Cpre%3E%3Ccode%3Eclass%20FileController%20&lt;%20Shivneri::Controller%20%0A%0A%20%20%20%20@%5BWorker%5D%0A%20%20%20%20@%5BRoute(%22/scripts/%7Bfile_name%7D.js%22)%5D%0A%20%20%20%20def%20get_scripts%0A%20%20%20%20%20%20%20%20file_name%20=%20param%5B%22file_name%22%5D%0A%20%20%20%20%20%20%20%20return%20text_result(%22file%20name%20is%20#%7Bfile_name%7D%22)%0A%20%20%20%20end%0A%0Aend%0A%3C/code%3E%3C/pre%3E`;
  title="Route";
